function decryptCipherText(){var c=$("#key").val();var b=$("#cipherText").val();if(c!=""){var a=GibberishAES.dec(b,c);$("#cipherBlock").replaceWith(a);$("div.description").siblings().hide();$("div.description").click(function(){$(this).siblings().toggle()})}}$(document).ready(function(){$("#key").focus();$("#cryptoForm").submit(function(a){decryptCipherText();a.preventDefault()})});